const v="ti2",e="디모데후서",t="2 Timothy",x=3,s=[{verse:1,text:"너는 이것을 알라 말세에 고통하는 때가 이르러"},{verse:2,text:"사람들이 자기를 사랑하며 돈을 사랑하며 자랑하며 교만하며 비방하며 부모를 거역하며 감사하지 아니하며 거룩하지 아니하며"},{verse:3,text:"무정하며 원통함을 풀지 아니하며 모함하며 절제하지 못하며 사나우며 선한 것을 좋아하지 아니하며"},{verse:4,text:"배신하며 조급하며 자만하며 쾌락을 사랑하기를 하나님 사랑하는 것보다 더하며"},{verse:5,text:"경건의 모양은 있으나 경건의 능력은 부인하니 이같은 자들에게서 네가 돌아서라"},{verse:6,text:"그들 중에 남의 집에 가만히 들어가 어리석은 여자를 유인하는 자들이 있으니 그 여자는 죄를 중히 지고 여러 가지 욕심에 끌린 바 되어"},{verse:7,text:"항상 배우나 끝내 진리의 지식에 이를 수 없느니라"},{verse:8,text:"얀네와 얌브레가 모세를 대적한 것 같이 그들도 진리를 대적하니 이 사람들은 그 마음이 부패한 자요 믿음에 관하여는 버림 받은 자들이라"},{verse:9,text:"그러나 그들이 더 나아가지 못할 것은 저 두 사람이 된 것과 같이 그들의 어리석음이 드러날 것임이라"},{verse:10,text:"나의 교훈과 행실과 의향과 믿음과 오래 참음과 사랑과 인내와"},{verse:11,text:"박해를 받음과 고난과 또한 안디옥과 이고니온과 루스드라에서 당한 일과 어떠한 박해를 받은 것을 네가 과연 보고 알았거니와 주께서 이 모든 것 가운데서 나를 건지셨느니라"},{verse:12,text:"무릇 그리스도 예수 안에서 경건하게 살고자 하는 자는 박해를 받으리라"},{verse:13,text:"악한 사람들과 속이는 자들은 더욱 악하여져서 속이기도 하고 속기도 하나니"},{verse:14,text:"그러나 너는 배우고 확신한 일에 거하라 너는 네가 누구에게서 배운 것을 알며"},{verse:15,text:"또 어려서부터 성경을 알았나니 성경은 능히 너로 하여금 그리스도 예수 안에 있는 믿음으로 말미암아 구원에 이르는 지혜가 있게 하느니라"},{verse:16,text:"모든 성경은 하나님의 감동으로 된 것으로 교훈과 책망과 바르게 함과 의로 교육하기에 유익하니"},{verse:17,text:"이는 하나님의 사람으로 온전하게 하며 모든 선한 일을 행할 능력을 갖추게 하려 함이라"}],r="2026-02-18T05:25:18.587Z",o={book:"ti2",bookName:e,bookNameEn:t,chapter:3,verses:s,fetchedAt:r};export{v as book,e as bookName,t as bookNameEn,x as chapter,o as default,r as fetchedAt,s as verses};
